import React from "react";
import axios from "axios";



export default function FriendInfo(props) {

  const getGenres = (name) => {
    let userPrefs = {}
    axios.get(`http://localhost:5000/api/${name}/genres`)
    .then(res => {
      userPrefs["genres"] = (res.data["genres"])
    })
    .catch(error => {console.log(error)})
    return(userPrefs)

  }



  return(
  <main className="favorite-form">
  <h2>{props.friend.name}'s preferences</h2>
  <div>{getGenres(props.friend.name)}</div>
  </main>
  )
}
