import React from "react";

export default function Genre(props) {
  let feelin = "love";
  return (
    <div className="genre">
      <img src={`images/genreicons/${props.name}.svg`} height='50px' alt={`${props.name}`} />
      <h4>{props.name}</h4>
      <button type="button" className={props.hate_class} onClick={() => props.setGenre(props.id, false), feelin="hate"}>Hate it!</button>
      <button type="button" className={props.meh_class} onClick={() => props.setGenre(props.id, "")}>Meh</button>
      <button type="button" className={props.love_class} onClick={() => props.setGenre(props.id, true), feelin="love"}>Love it!</button>
      {feelin==="love" && 
      <img src="images/love.png"/>}
    </div>
  )
}
