import React from "react";
import axios from "axios";



export default function FriendInfo(props) {
  let lovedGenres = []
  let hatedGenres = []
  const getGenres = (name) => {
    axios.get(`http://localhost:5000/api/${name}/genres`)
    .then(res => {
      for (let genre of res.data["genres"]) {
        if (genre["preference"] === true) {
          lovedGenres.push(genre["id"])
        } else if (genre["preference"] === false) {
          hatedGenres.push(genre["id"])
        }
      }
    })
  }
  blah = []
  getGenres(props.friend.name)
  for (let genre of lovedGenres) {
    blah.push(
      <Genre
        key={genre.id}
        id={genre.id}
        name={genre.name}
        love_class={lovedGenres.includes(genre.id)? "selected": "unselected"}
        meh_class={lovedGenres.includes(genre.id)? "unselected": hatedGenres.includes(genre.id)? "unselected" : "selected"}
        hate_class={hatedGenres.includes(genre.id)? "selected": "unselected"}
        setGenre={props.setGenre}
      />  
    )
 }
  return(
  <main className="favorite-form">
  <h2>{props.friend.name}'s preferences</h2>

  </main>
  )
}
