import React from "react";
import Popup from "reactjs-popup";
import './Nav.scss'
import Signup from "./Signup"
import Login from "./Login"

const contentStyle = {
  width: "300px",
  height: "300px",
  borderRadius: "20px"
};

const specialStyle={
  backgroundColor:"transparent",
  text: "white"

}
export default function Form(props) {
  return (
    <>
      <Popup trigger=
        {
          props.createUser ? <button className="list_name" style={specialStyle}>register</button> : <button className="list_name" style={specialStyle}>sign in</button>
        } modal
        contentStyle={contentStyle}>
        {props.createUser && <Signup createUser={props.createUser} />}
        {props.onLogin && <Login onLogin={props.onLogin} />}
      </Popup>
    </>
  );
}
