import React, { useEffect, useState } from "react";
import axios from "axios";





export default function FriendInfo(props) {
  const [prefs, setPrefs] = useState('')
  useEffect(() => {
    axios.get(`http://localhost:5000/api/friend${props.friend}/genres`)
    .then(res => {   
      setPrefs(res.data)
    })
    .catch(error => {console.log(error)})
  }, [])
  let loves = []
  let hates = []
  for (let pref in prefs) {
    for (let each of pref) {
      if (each === 'hate') {
        hates.push(`images/genreicons/${each}.svg`)
      } else {
        loves.push(`images/genreicons/${each}.svg`)
      } 
    }
  }
  return(
  <main className="favorite-form">
  <h2>{props.friend}'s preferences</h2>
  <div>{hates}</div>
  <div>{loves}</div>
  </main>
  )
}
