import React from "react";
// import Logon from "./Logon"
import User from "./User"
import Form from "./Form";
let classnames = require("classnames");

export default function Nav(props) {

  const logOn = (name, password) => {
    return props.getUser(name, password);
  }

  const logOut = () => {
    props.removeUser();
  }


  const buttonClass = classnames("list-name", {
    "list-name-show": props.favList==="show",
    "list-name": props.favList==="hide"
  });
  // for (let each of [props.favList, props.laterList]) {
  //   if (each === "show"){
  //     buttonClass += '-show'
  //   }
  // }

  const toggleList = function(status) {
    if (status === "show") {
      return "hide" 
    } else {
      return "show"
    }
  }

  return (
    <div className="nav">
      <div className="logo">
        <img id="cinema-logo"  src="images/popcorn.png" width="150px" alt="Social Cinema" />
        <button className={buttonClass} disabled={!props.user} onClick={() => props.setFavList(toggleList)}>
          Favorite Movies
        </button>
        <button className={buttonClass} disabled={!props.user} onClick={() => props.setLaterList(toggleList)}>
          Later Movies
        </button>
        <button className={buttonClass} onClick={() => props.setGenreList(toggleList)}>
          My Preferences
        </button>
        <button className={buttonClass} disabled={!props.user} onClick={() => props.setFriendList(toggleList)}>
          My Friends
        </button>
      </div>
      <div className="user-login"> 
        {props.user === "" ? <><Form onLogin={logOn} /> <Form createUser={props.createUser} /></> : <User user={props.user} logout={() => logOut()} />}
      </div>

    </div>
  );
}
