import React, { useEffect, useState } from "react";
import axios from "axios";





export default function FriendInfo(props) {
  const [prefs, setPrefs] = useState('')
  useEffect(() => {
    axios.get(`http://localhost:5000/api/friend${props.friend}/genres`)
    .then(res => {   
      setPrefs(res.data)
    })
    .catch(error => {console.log(error)})
  }, [])
  let loves = []
  let hates = []
  console.log(prefs["love"])
  for (let each in prefs["love"]){
    loves.push(prefs["love"][each])
  }
  console.log(loves)
  return(
  <main className="favorite-form">
  <h2>{props.friend}'s preferences</h2>
  {/* <div>{hates}</div> */}
  <div>{loves}</div>
  </main>
  )
}
